const projectData=[{id:"proj-optica",title:"Optica Medica",url:"https://www.opticamedicabuga.com/",imgSml:"optica-ipad.png",imgBig:"optica.png",blurb:"A complete revamp of an old website, which was unresponsive and unreadable on mobile devices.",languages:"Photoshop | JavaScript | HTML | CSS | SCSS"},{id:"proj-rtg",title:"RTG Photography",url:"https://www.rtgphotography.com.au/",imgSml:"rtg-ipad.png",imgBig:"rtg-photography.png",blurb:"A bespoke, responsive website designed and created for a local travel photographer",languages:"Photoshop | Carva | UI Kit | PHP | HTML | CSS | cPanel | Google Analytics"},{id:"proj-mini",title:"Mini Projects",url:"https://sgedye.github.io/mini-projects/",imgSml:"mini-ipad.png",imgBig:"mini-projects.png",blurb:"Various projects undertaken for personal interest, continuous learning and professional development",languages:"React, JavaScript, jQuery, Syled-Components, Bootstrap, HTML, CSS, SCSS, Photoshop"}];let projectList=document.createElement("div");projectList.id="project-list",projectData.map(e=>{let t=document.createElement("div"),o=`${e.title} logo`.replace(/\s/g,"-"),i=screen.width<1200?`../assets/projects/${e.imgSml}`:`../assets/projects/${e.imgBig}`;t.id=e.id,t.key=e.id,screen.width<768?(t.classList.add("project","flip-container"),t.innerHTML=`\n      <div class="flip-card">\n        <div class="face front-face">\n          <img src=${i} width="auto" height="350" alt=${o} />\n        </div>\n        <div class="project-info face back-face">\n          <h3>${e.title}</h3>\n          <p>${e.blurb}</p>\n          <code>${e.languages}</code>\n          <a href=${e.url} target="_blank" rel="noopener noreferrer">\n            <button>View</button>\n          </a>\n        </div>\n      </div>\n    `):(t.classList.add("project"),t.innerHTML=`\n      <a href=${e.url} target="_blank" rel="noopener noreferrer">\n        <img class="project-logo" src=${i} width="auto" height="350" alt=${o} />\n        <div class="project-info">\n          <h3>${e.title}</h3>\n          <p>${e.blurb}</p>\n          <code>${e.languages}</code>\n          <object><button class="project-btn">View Website</button></object>\n        </div>\n      </a>\n    `),projectList.appendChild(t)}),document.getElementById("project-contents").appendChild(projectList);